(134, 1, '2025-03-26 14:37:45', '2025-03-26 11:37:45', '<!-- wp:html -->\n<!DOCTYPE html>\n<html lang=\"ru\">\n<head>\n    <meta charset=\"UTF-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n    <title>Сортировщик вакансий</title>\n    <style>\n        body {\n            font-family: Arial, sans-serif;\n            margin: 20px;\n        }\n        button {\n            padding: 10px 20px;\n            font-size: 16px;\n            cursor: pointer;\n        }\n        .vacancies {\n            display: none;\n            margin-top: 20px;\n        }\n        .vacancies.active {\n            display: block;\n        }\n        .field-selection {\n            display: none;\n            margin-top: 20px;\n        }\n        .field-selection.active {\n            display: block;\n        }\n    </style>\n</head>\n<body>\n\n<button id=\"openModal\">Выбрать вакансии</button>\n\n<div class=\"field-selection\" id=\"fieldSelection\">\n    <h2>Выберите сферу и зарплату</h2>\n    <label for=\"field\">Сфера:</label>\n    <select id=\"field\" onchange=\"showVacancies()\">\n        <option value=\"\">--Выберите сферу--</option>\n        <option value=\"it\">Айти</option>\n        <option value=\"management\">Менеджмент</option>\n    </select>\n    <br>\n    <label for=\"salary\">Минимальная зарплата:</label>\n    <input type=\"number\" id=\"salary\" placeholder=\"Введите сумму\">\n    <br>\n    <button onclick=\"showVacancies()\">Показать вакансии</button>\n</div>\n\n<div class=\"vacancies\" id=\"itVacancies\">\n    <h3>Вакансии в сфере Айти:</h3>\n    <ul>\n        <li>Тестировщик: Зарплата от 60,000</li>\n        <li>Разработчик: Зарплата от 80,000</li>\n        <li>Архитектор баз данных: Зарплата от 100,000</li>\n        <li>Системный администратор: Зарплата от 70,000</li>\n    </ul>\n</div>\n\n<div class=\"vacancies\" id=\"managementVacancies\">\n    <h3>Вакансии в сфере Менеджмента:</h3>\n    <ul>\n        <li>Продюсер: Зарплата от 90,000</li>\n        <li>Специалист по бизнес-процессам: Зарплата от 80,000</li>\n        <li>Девелопер: Зарплата от 75,000</li>\n    </ul>\n</div>\n\n<script>\n    document.getElementById(\'openModal\').onclick = function() {\n        document.getElementById(\'fieldSelection\').classList.toggle(\'active\');\n    }\n\n    function showVacancies() {\n        const field = document.getElementById(\'field\').value;\n        const salary = document.getElementById(\'salary\').value;\n        const itVacancies = document.getElementById(\'itVacancies\');\n        const managementVacancies = document.getElementById(\'managementVacancies\');\n\n        itVacancies.classList.remove(\'active\');\n        managementVacancies.classList.remove(\'active\');\n\n        if (field === \'it\') {\n            itVacancies.classList.add(\'active\');\n        } else if (field === \'management\') {\n            managementVacancies.classList.add(\'active\');\n        }\n    }\n</script>\n\n</body>\n</html>\n<!-- /wp:html -->', 'жужа', '', 'inherit', 'closed', 'closed', '', '126-revision-v1', '', '', '2025-03-26 14:37:45', '2025-03-26 11:37:45', '', 126, 'http://mysite.rus/?p=134', 0, 'revision', '', 0);
