play: block; /* Показываем меню при наведении */\n        }\n    </style>\n</head>\n<body>\n\n<div class=\"dropdown\">\n    <button class=\"dropbtn\">Меню</button>\n    <div class=\"dropdown-content\">\n        <a href=\"http://mysite.rus/?page_id=25\" target=\"_blank\">Главная</a>\n        <a href=\"http://mysite.rus/sample-page/\" target=\"_blank\">О нас</a>\n        <a href=\"http://mysite.rus/?page_id=42&preview=true\" target=\"_blank\">Контакты</a>\n    </div>\n</div>\n\n</body>\n</from>\n</html>\n<!-- /wp:html -->\n\n<!-- wp:html -->\n</from align=\"center\">\n<!DOCTYPE html>\n<html lang=\"ru\">\n<head>\n    <meta charset=\"UTF-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n    <title>Сортировщик вакансий</title>\n    <style>\n        body {\n            font-family: Arial, sans-serif;\n            margin: 20px;\n        }\n        button {\n            padding: 10px 20px;\n            font-size: 16px;\n            cursor: pointer;\n            background-color: black;\n            color: white;\n            border: none;\n            border-radius: 5px;\n        }\n        .modal {\n            display: none;\n            position: fixed;\n            top: 0;\n            left: 0;\n            width: 100%;\n            height: 100%;\n            background-color: rgba(0, 0, 0, 0.7);\n            justify-content: center;\n            align-items: center;\n        }\n        .modal-content {\n            background-color: white;\n            padding: 20px;\n            border-radius: 5px;\n            width: 300px;\n            text-align: center;\n        }\n        .vacancies {\n            display: none;\n            margin-top: 20px;\n        }\n        .vacancies.active {\n            display: block;\n        }\n        select, input {\n            margin-top: 10px;\n            padding: 5px;\n            width: 100%;\n        }\n        .close {\n            cursor: pointer;\n            color: red;\n            marg