on class=\"arrow left-arrow\" onclick=\"changeSlide(-1)\">&#10094;</button>\n    <button class=\"arrow right-arrow\" onclick=\"changeSlide(1)\">&#10095;</button>\n</div>\n\n<script>\n    let currentSlide = 0;\n\n    function showSlide(index) {\n        const slides = document.querySelector(\'.slides\');\n        const totalSlides = document.querySelectorAll(\'.slide\').length;\n        if (index >= totalSlides) {\n            currentSlide = 0;\n        } else if (index < 0) {\n            currentSlide = totalSlides - 1;\n        } else {\n            currentSlide = index;\n        }\n        slides.style.transform = \'translateX(\' + (-currentSlide * 100) + \'%)\';\n    }\n\n    function changeSlide(direction) {\n        showSlide(currentSlide + direction);\n    }\n\n    // Автоматическая смена слайдов (опционально)\n    setInterval(() => {\n        changeSlide(1);\n    }, 5000); // смена каждые 5 секунд\n</script>\n\n</body>\n</html>\n<!-- /wp:html -->\n\n<!-- wp:group {\"metadata\":{\"categories\":[\"footer\"],\"patternName\":\"twentytwentytwo/footer-social-copyright\",\"name\":\"Подвал с ссылками на социальные сети и авторскими правами\"},\"align\":\"full\",\"layout\":{\"inherit\":true,\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group alignfull\"><!-- wp:group {\"align\":\"wide\",\"style\":{\"spacing\":{\"padding\":{\"top\":\"4rem\",\"bottom\":\"4rem\"}}}} -->\n<div class=\"wp-block-group alignwide\" style=\"padding-top:4rem;padding-bottom:4rem\"><!-- wp:social-links {\"iconColor\":\"foreground\",\"iconColorValue\":\"var(\\u002d\\u002dwp\\u002d\\u002dpreset\\u002d\\u002dcolor\\u002d\\u002dforeground)\",\"iconBackgroundColor\":\"background\",\"iconBackgroundColorValue\":\"var(\\u002d\\u002dwp\\u002d\\u002dpreset\\u002d\\u002dcolor\\u002d\\u002dbackground)\",\"layout\":{\"type\":\"flex\",\"justifyContent\":\"center\"}} -->\n<ul class=\"wp-block-social-links has-icon-color has-icon-background-color\"><!-- wp:social-link {\"url\":\"#\",\"service\":\"facebook\"} /-->\n\n<!-- wp:social-link {\"url\":\"#\",\"service\":\"twitter\"} /-->\n\n<!-- wp:social-link {\"url\":\"#\",\"service\":\"instagram\"} /--></ul>\n<!-- /wp:social-links -->\n\n<!-- wp:html -->\n<section class=\"map\">\n<h4>Как нас найти?</h4>\n<iframe\nsrc=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2244.484706528756!2d37.6173!3d55.7558!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x414ab1234567890!2z0J_RgNC40LLQtdGC!5e0!3m2!1sru!2sru!4v1614874567890\"\nwidth=\"100%\"\nheight=\"400\"\nstyle=\"border:0;\"\nallowfullscreen=\"\"\nloading=\"lazy\">\n</iframe>\n</section>\n<!-- /wp:html --></div>\n<!-- /wp:group --></div>\n<!-- /wp:group -->\n\n<!-- wp:paragraph -->\n<p><em><br>Адрес офиса: г. Санкт-Петербург, Проспект Авиаконструкторов, 69</em></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n<!DOCTYPE html>\n<html lang=\"ru\">\n<head>\n    <meta charset=\"UTF-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n    <title>Позвоните нам</title>\n    <style>\n        body {\n            font-family: Arial, sans-serif;\n            margin: 20px;\n        }\n        .call-button {\n            padding: 10px 20px;\n            font-size: 16px;\n            cursor: pointer;\n            background-color: black;\n            color: white;\n            border: none;\n            border-radius: 5px;\n            text-decoration: none;\n            display: inline-block;\n        }\n    </style>\n</head>\n<body>\n\n<a href=\"tel:+79052019767\" class=\"call-button\">Позвоните нам</a>\n\n</body>\n</html>\n<!-- /wp:html -->', '', '', 'inherit', 'closed', 'closed', '', '25-revision-v1', '', '', '2025-03-26 16:51:00', '2025-03-26 13:51:00', '', 25, 'http://mysite.rus/?p=175', 0, 'revision', '', 0);
